import React, { useState } from 'react'
import './AdminHome.css'
import { db } from '../firebase'

import {BusinessCenter, Collections, Flight, GroupWork, Home, Inbox, Language, People, Person, ShowChart } from '@material-ui/icons';
import MainDash from './MainDash';
import { Link, Route } from 'react-router-dom';
import ViewCustomers from './ViewCustomers';
import ViewEmployees from './ViewEmployees';

/*
    1. DELETE USERS
    2. UPDATE USER INFO
    3. SORT USERS
    4. SEARCH USERS
*/
export default function AdminHome() {

    const [users, setUsers] = useState([]);
    const [team, setTeam] = useState([]);
    let customers = []
    let admin = []

    const getUsers = async () => {
        const customersRef = db.collection('user')
        customersRef
            .orderBy('fname')
            .onSnapshot( snapshot => {
                let changes = snapshot.docChanges();

                changes.forEach( change => {

                    if(change.type === 'added'){
                        // console.log("Added: ", change.doc.data())

                        // let updatedCus = [...customers];
                        let userExists = users.find( cus => change.doc.data().userID === cus.id);

                        if (!userExists){
                            change.doc.data().type === "customer" ?
                                customers.push(change.doc.data())
                                : change.doc.data().type === "admin" && 
                                admin.push(change.doc.data())
                            setUsers(customers)
                            setTeam(admin)
                        }
                    }else if(change.type === 'removed'){

                        const userIndex = customers.findIndex( customer  => change.doc.data().userID === customer.userID)

                         if (userIndex  >= 0) {
                            customers.splice(userIndex , 1);
                            change.doc.data().type === "customer" ?
                                customers.push(change.doc.data())
                                : change.doc.data().type === "admin" && 
                                admin.push(change.doc.data())
                            setUsers(customers)
                            setTeam(admin)
                            }                                               
                    }else{
                        console.log("Change: ", change)

                    }
                })
                // setUsers(customers)
                // setTeam(admin)
            })   
    }

    // TO RUN FUNCTION ON PAGE RELOAD
    // (window.location.pathname === '/admin/customers' || window.location.pathname === '/admin/team') &&
    //     getUsers()

    // DELETION
    // 
    const deleteRecord = (id) => {
    }
    const handleSelection = (e) => {

    }
    return (
        <section className="admin_main_body">
            <section className="admin_sidebar">
            <div className="sidebar_top">
                <img src="images/cafe_kora.png" alt="Logo" />
            </div> 

            <div className="sidebar_bottom">
                <p className="sidebar_parts" onClick={handleSelection}>
                    <Home className="sidebar_icons"/>
                    <Link to="/admin">Home</Link>
                </p>

                <p className="sidebar_parts" onClick={getUsers}>
                    <People className="sidebar_icons"/>
                    <Link to="/admin/customers">Customers</Link>
                </p>

                <p className="sidebar_parts" onClick={getUsers}>
                    <GroupWork className="sidebar_icons"/>
                    <Link to="/admin/team">Cafe Kora Team</Link>
                </p>
                <p className="sidebar_parts" onClick={handleSelection}>
                    <Collections className="sidebar_icons"/>
                    <Link to="/admin/products">Products</Link>
                </p>

                <p className="sidebar_parts" onClick={handleSelection}>
                    <ShowChart className="sidebar_icons"/>Analytics
                </p>

                <p className="sidebar_parts" onClick={handleSelection}>
                    <Inbox className="sidebar_icons"/>Categories
                </p>

                <p className="sidebar_parts" onClick={handleSelection}>
                    <BusinessCenter className="sidebar_icons"/>Employees
                </p>

                <p className="sidebar_parts" onClick={handleSelection}>
                    <Person className="sidebar_icons"/>Profile
                </p>

                <p className="sidebar_parts" onClick={getUsers}>
                    <Language className="sidebar_icons"/>
                    <Link to="/">Website Home</Link>
                </p>          
            </div>

            </section>

            <div className="dashboard_options_cont">
                {/* PUSH DATA ACCORDING TO THE USER CLICKS */}
                <Route exact path="/admin" component={MainDash} />
                <Route exact path="/admin/customers">
                    <ViewCustomers getUsers={getUsers} deleteUsers={deleteRecord} customers={users}/>
                </Route> 
                <Route exact path="/admin/team">
                    <ViewEmployees team={team}/>
                </Route>                 
            </div>
        </section>
    )
}
