import { db } from './firebase'

export const initialState = {
    users: [],
    products: []

};


export const reducer = async (prevState = initialState, action) => {
    const customersRef = db.collection('user')
    const productsRef = db.collection('product')


    switch(action.type) {

        case "delete_product" :
            let copyProducts;
            try{ 
                copyProducts = [...prevState.products]

                customersRef
                    .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            productsRef.doc(user.id).delete() 
                        })
                    })
                    .then( () => {
                        return copyProducts.filter( product => product.product_id !== action.targetID);
                    })
                    .catch(error => console.log("Deletion Error: ", error))         
            }catch(error){
                console.log("Deletion Error: ", error)
            }            
            return {
                ...prevState, 
                products: copyProducts,                
            }
        case "fetch_products":
            let fetchedProducts;
            try{
                fetchedProducts = [...prevState.products, []]
                console.log(action)
                productsRef 
                .onSnapshot((snapshot) => {
                    snapshot.docs.forEach( item => {
                        if(fetchedProducts.length === 0){
                            fetchedProducts.push(item.data())
                        }else{
                            let itemExists = fetchedProducts.findIndex( product => product.product_id === item.data().product_id)

                            if(itemExists < 0){
                                fetchedProducts.push(item.data())
                            }
                        }
                    })
                })
            }catch(error){
                console.log(error)
            }
            return{
                ...prevState, 
                products: fetchedProducts,
            }

        case "fetch_user_details":
            let copyUsers = [...prevState.users]

            let selectedUser;
            customersRef
                .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            selectedUser = user.data()
                            return selectedUser
                        })
                    })
                    .catch( (error) => console.log("Update Error: ", error))
            console.log(action, selectedUser)
            return{
                ...prevState,
                users: copyUsers,
            }


        default: 
            return prevState;
    }
            // return prevState;

}
