import React from 'react'
import { useEffect } from 'react'
import './ViewCustomers.css'
// import { useAuth } from '../contexts/AuthContext'
import { db } from '../firebase'

import {Delete } from '@material-ui/icons';

export default function ViewCustomers({deleteUsers, customers}) {

    useEffect( () => {

    }, [])
    // const { currentUser} = useAuth(); 

    // deleteUsers = (e) => e.target.parentNode.parentNode.parentNode.id;  
    const userTable = db.collection("user");

    const handleDelete = async (e) => {
        e.preventDefault();
        let delUserID = e.target.parentNode.parentNode.parentNode.id;
    // console.log(userTable.doc())

        try{ 
            console.log("User ID Clicked: ", delUserID)

            console.log(customers)
            // const ref = await db.collection('user').doc(delUserID);

            // ref.get().then( doc => {
            //     if(doc.exists){

            //     }
            // })
            // console.log("User: ", delUserID," Deleted")
        }catch(error){
            console.log("Error: ", error)
        }

    }

    return (
        <>
            <div className="welcome_bar">
            <h4 className="welcome_text">
                Cafe Kora Customers 
            </h4>
            <p className="welcome_ptag">Total Customers: {customers?.length}</p>

        </div>
        {

            customers?.length ?
        <>

        <div className="operations_section">

        </div>
        <div className="display_area">
            <table className="customers_table">
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email Address</th>
                        <th>Phone Number</th>
                        <th>Date Joined</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                {
                    customers.map( customer => 
                        <tr key={customer.userID} id={customer.userID}>
                            <td>{customer.fname}</td>
                            <td>{customer.lname}</td>
                            <td>{customer.email}</td>
                            <td>{customer.phone}</td>
                            <td>{customer.datejoined.toDate().toDateString()}</td>
                            <td><Delete onClick={handleDelete}/></td>
                        </tr>
                    )
                }
                </tbody>    

            </table>
        </div> 
        </>
            :
            <div className="welcome_bar">
                <p className="welcome_ptag">Loading Customers...</p>            
            </div>            
        }


        </>
    )
}
