import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './Login.css'
import AdminHome from './adminComponents/AdminHome';

export default function AddProducts() {

    const [error, setError] = useState();
    const [isError, setIsError] = useState(false);

    // const [prodDetails, setProductDetails] = ({ 
    //     "product_image" : "",
    //     "product_title" : "",
    //     "product_descr" : "",
    //     "product_cat" : "",
    //     "product_price" : 0,
    //     "product_stockqty": 0
    // })

    const loadImage = (event) => {
        const image = document.querySelector("#display_prodImage");
        if(event.target.files[0]){
            image.src = URL.createObjectURL(event.target.files[0]);
        }else {

        }
    }

    const handleChange = (e) => {

    }

    const handleSubmit = (e) => {

    }

    return (
        <section className="">

        <section className="login_section reg_section"> 

            <h2>Cafe Kora Add Product</h2>

            <form onSubmit={handleSubmit} className="registration_form">
                {
                isError &&
                <div className="form_error form_inputs">
                    <p>{error}</p>
                </div> 

                }
                 <label className="form_labels">Upload Product Image:</label>
                <img id="display_prodImage" alt="" />	

                <input onChange={loadImage} type="file" className="form_inputs" name="product_image" accept="image/*"/>               
                <label className="form_labels">Product Name:</label>
                <input onChange={handleChange} type="text" className="form_inputs" name="product_name" />
                <label className="form_labels">Product Description:</label>
                <input onChange={handleChange} type="text" className="form_inputs" name="product_descr" />
                <label className="form_labels">Product Category:</label>
                <select onChange={handleChange} className="form_inputs" id="select_cat">
                    <option value="">Select Category:</option>
                    <option value="Bread">Bread</option>
                    <option value="Pastry">Pastry</option>
                </select>
                <label className="form_labels">Product Price:</label>
                <input onChange={handleChange} type="" className="form_inputs" name="product_price" />

                {/* <label className="form_labels">Password:</label>
                <input onChange={handleChange} type="password" className="form_inputs" name="registerPass" />
                <label className="form_labels">Confirm Password:</label>
                <input onChange={handleChange} type="password" className="form_inputs" name="registerConfirmPass" password="" autoComplete="new-password" /> */}

                <button type="submit" className="form_btn ">Add Product</button>
            </form>
            <div className="login_footer">
                <p>Already have an account?
                    <Link to="/login"> Sign In</Link>
                </p>
                <div className="back_home_div">
                    <Link to="/">
                    <AdminHome />Back Home
                    </Link>
                </div>
            </div>
        </section>             
        </section>
    )
}
