import React from 'react'
import { Bar, Pie } from 'react-chartjs-2';

export default function Chart() {
    let chartData = {
                labels: ["January", "February", "March", "April", "May", "June", "July"],
                datasets: [
                    {
                    label: "My First dataset",
                    backgroundColor: 'rgb(255, 99, 132)',
                    borderColor: 'rgb(255, 99, 132)',
                    data: [0, 10, 5, 2, 20, 30, 45],
                    }
                ]
            }
    // PRODUCTS OPTIONS - TRY PIECHART

    let productData = {
        labels: ["Breads", "Pastries"],
        datasets: [{
            data: [78, 90],
            backgroundColor: ["#8e5ea2","#c45850"],

        }]

    }
    return (
        <section className="chart_section">
            {/* <Bar data={chartData} height={"400px"} width={"500px"} options={{ maintainAspectRatio: false }}
            /> */}
            <Pie
                data={productData} height={"500px"} width={"500px"}
                    options={{
                        responsive: true,
                        title: { 
                            display: true,
                            text: "Projected Product Sales in 2021"
                             },
                        // scales:{
                        //     yAxes:{
                        //         ticks:{
                        //             beginAtZero: true
                        //         }
                        //     }
                        // }
                    }}                
            />
        </section>
    )
}
