import React, { useEffect, useState } from 'react'
import './ViewCustomers.css'
import { db } from '../firebase'

import {Create, Delete } from '@material-ui/icons';
import { useStateValue } from '../StateProvider';

export default function ViewCustomers({ customers }) {

    const [currentCustomers, setCurrentCustomers] = useState();
    const [isLoading, setIsLoading] = useState(false);

    const customersRef = db.collection('user')

    const [ { users }, dispatch] = useStateValue();

    // const trackChange =  () => {
    //     customersRef.onSnapshot( snapshot => {
    //         snapshot.docChanges().forEach( (change) => {
    //             if(change.type === "added"){
    //                 console.log(change.doc.data())
    //             }else if(change.type === "removed"){
    //                 console.log(change)
    //             }else if(change.type === "modified"){

    //             }

    //         })
    //     })
    // }    

    useEffect(() => {
        if(currentCustomers === undefined){
            setCurrentCustomers(customers)
        }else{
            let setObj = new Set();

            let result = currentCustomers.reduce((acc,item)=>{
            if(!setObj.has(item.userID)){
                setObj.add(item.userID,item)
                acc.push(item)
            }
            return acc;
            },[]); 
            setCurrentCustomers(result)

        }

    }, [])

    const handleDelete = async (e) => {
        e.preventDefault();
        let targetID = e.target.parentNode.parentNode.parentNode.id;
        if(window.confirm("Are You Sure you want to delete this record")){
            try{ 
                customersRef
                    .where("userID", "==", `${targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            customersRef.doc(user.id).delete() 
                        })
                    })
                    .then( () => {
                        const filtered = customers.filter( (user) => user.userID !== targetID );
                        setCurrentCustomers([...new Set([...filtered])])
                    })
                    .catch(error => console.log("Deletion Error: ", error))         
            }catch(error){
                console.log("Deletion Error: ", error)
            }
        }
    }

    const handleUpdate = async (e) => {
        let targetID = e.target.parentNode.parentNode.parentNode.id;
        console.log(e.target)
        dispatch({
            type: "update_user",
            id: targetID
        }) 
    }


    return (
        <>
            <div className="welcome_bar">
            <h4 className="welcome_text">
                Cafe Kora Customers 
            </h4>
            <p className="welcome_ptag">Total Customers: {currentCustomers?.length}</p>

        </div>
        {

            currentCustomers?.length ?
        <>

        <div className="operations_section">

        </div>
        <div className="display_area">
            <table className="customers_table">
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email Address</th>
                        <th>Phone Number</th>
                        <th>Date Joined</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                {
                    currentCustomers.map( customer => 
                        <tr key={customer.userID} id={customer.userID}>
                            <td>{customer.fname}</td>
                            <td>{customer.lname}</td>
                            <td>{customer.email}</td>
                            <td>{customer.phone}</td>
                            <td>{customer.datejoined.toDate().toDateString()}</td>
                            <td>
                            <Delete onClick={handleDelete} className="action_icons"/>
                            <Create onClick={handleUpdate}
                                className="action_icons"
                            />
                            </td>
                        </tr>
                    )
                }
                </tbody>    

            </table>
        </div> 
        </>
            :
            <div className="welcome_bar">
                <p className="welcome_ptag">Loading Customers...</p>            
            </div>            
        }


        </>
    )
}
