import { Create, Delete, Home } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import { Link, useHistory } from 'react-router-dom';
import { useStateValue } from '../StateProvider'
import './Update.css'

export default function Update({match}) {

    const [ { products }] = useStateValue();
    const [currentP,setCurrentP ] = useState();
    const [isUpdate, setIsUpdate] = useState(false);
    const [uploadImage, setUploadImage] = useState();

    // ERROR HANDLING
    const [error, setError] = useState();
    const [isError, setIsError] = useState(false);

    products.map( prod => {
        if(prod.product_id === match.params.id){
            return prod
        }
    setCurrentP(prod)   
    })

    console.lo(match.params.id)


    const handleUpdate = () => {

    }

    const loadImage = (event) => {
        event.preventDefault();
        const image = document.querySelector("#display_userImage");
        if(event.target.files[0] !== undefined){
            image.src = URL.createObjectURL(event.target.files[0]);
            image.style.display = "block"
            setUploadImage(event.target.files[0])
        }else {
            image.src = "/images/avatar_placeholder.png";
        }
    }

    const handleChange = input => async (e) => {
        (input === "product_image") && loadImage(e)
    } 

    return (
        <>
        { 
            !isUpdate ?
            <section className="update_section up_prod"> 

                <h2 className="add_prod_title">Cafe Kora Add Product</h2>

                <form onSubmit={handleUpdate} className="update_column">

                    <div className="update_left registration_form">
                        <label className="form_labels">Upload Profile Picture:</label>
                        <img id="display_userImage" src="/images/avatar_placeholder.png" alt="Upload" />	

                        <input onChange={handleChange("product_image")} type="file" className="form_inputs" name="product_image" accept="image/*"/> 
                    </div>

                    <div className="update_right registration_form">
                        {
                        isError &&
                        <div className="form_error form_inputs">
                            <p>{error}</p>
                        </div> 

                        }
                        <label className="form_labels">Product Name:</label>
                        <input onChange={handleChange("product_name")} type="text" className="form_inputs" name="product_name"/>
                        <label className="form_labels">Product Description:</label>
                        <input onChange={handleChange("product_descr")} type="text" className="form_inputs" name="product_descr" />
                        <label className="form_labels">Product Category:</label>
                        <select onChange={handleChange("product_cat")} className="form_inputs" id="product_cat">
                            <option value="">Select Category:</option>
                            <option value="breads">Bread</option>
                            <option value="pastries">Pastry</option>
                        </select>
                        <label className="form_labels">Product Price:</label>
                        <input onChange={handleChange("product_price")} type="text" className="form_inputs" name="product_price" />

                        <button type="submit" className="form_btn ">Add Product</button>

                    </div>


                </form>
                    <div className="login_footer back_home_div add_footer">
                        <Link to="/"><Home />Back Home</Link>
                    </div>
            </section>        

            :
            <section className="update_section up_prod"> 

            <h2 className="add_prod_title">Cafe Kora Update Products</h2>

            <form onSubmit={handleUpdate} className="update_column">

                <div className="update_left registration_form">
                    <label className="form_labels">Upload Product Photo:</label>
                    <img id="display_userImage" src={currentP.product_image} alt="Upload" />	

                    <input onChange={handleChange("product_image")} type="file" className="form_inputs" name="product_image" id="product_image" accept="image/*"/> 
                </div>

                <div className="update_right registration_form">
                    {
                    isError &&
                    <div className="form_error form_inputs">
                        <p>{error}</p>
                    </div> 

                    }
                    <label className="form_labels">Product Name:</label>
                    <input 
                    onChange={handleChange("product_name")} 
                    placeholder={currentP.product_name}
                    type="text" className="form_inputs" id="product_name" name="product_name" />
                    <label className="form_labels">Product Description:</label>
                    <input onChange={handleChange("product_descr")} type="text" className="form_inputs" name="product_descr" placeholder={currentP.product_descr}/>
                    <label className="form_labels">Product Category:</label>
                    <select onChange={handleChange("product_cat")} className="form_inputs" id="product_cat">
                        <option value="">Select Category:</option>
                        <option value="breads">Bread</option>
                        <option value="pastries">Pastry</option>
                    </select>
                    <label className="form_labels">Product Price:</label>
                    <input onChange={handleChange("product_price")} type="text" className="form_inputs" name="product_price" placeholder={currentP.product_name}/>

                    <button type="submit" className="form_btn ">Update Product</button>

                </div>


                </form>
                <div className="login_footer back_home_div add_footer">
                    <Link to="/"><Home />Back Home</Link>
                </div>
            </section>        

        }
        </>
    )
}
