import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import './Login.css'
import { useAuth } from '../contexts/AuthContext'

export default function Login() {
    const history = useHistory();

    const { loginUser } = useAuth(); 

    const [ loginEmail, setLoginEmail ] = useState();
    const [ loginPass, setLoginPass] = useState();
    const [ loginError, setLoginError] = useState();    

    return (
        <section class="login_section">
            <div class="login_logo">
                {/* <!-- <img src="images/logo.png" alt="Site Logo" /> --> */}
            </div>
            <h2>Customer Log In</h2>
            <form class="login_form">

                <label class="form_labels">Email:</label>
                <input type="email" id="form_input" class="form_inputs" name="login_email" />
                <label class="form_labels">Password:</label>
                <input type="password" id="form_input" class="form_inputs" name="login_pass" password="" />

                <button type="submit" class="form_btn ">Log In</button>
            </form>

            <div class="login_footer">
                <p>Are you a new user?
                    <Link to="/register.html">Sign Up</Link>
                </p>
                <Link class="back_home" to="/"><i class="fas fa-chevron-left"></i>Back Home</Link>
            </div>
        </section>
    )
}
