import React, { useEffect, useState } from 'react';
import "./App.css";
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom'
import { AuthProvider } from "./contexts/AuthContext"
import { storage } from "./components/firebase"

import Login from './components/Login';
import Header from './components/Header';
import HomePage from './components/HomePage';
import Breads from './components/Breads';
import Pastries from './components/Pastries';
import Register from './components/Register';
import AdminHome from './components/adminComponents/AdminHome';
import Role from './components/Role';
import AddProducts from './components/AddProducts';
import Profile from './components/Profile';

export default function App(){

  const [pastries, setPastries] = useState([]);
  const [breads, setBreads] = useState([]);

  // List All Files
  const listItem = () => {
    storage.ref().child('products/breads').listAll()
      .then(res => {
        res.items.forEach((item) => {
          setBreads(arr => [...arr, item.name]);
        })
      })
      .catch(err => {
        alert(err.message);
      })
    storage.ref().child('products/pastries').listAll()
      .then(res => {
        res.items.forEach((item) => {
          setPastries(arr => [...arr, item.name]);
        })
      })
      .catch(err => {
        alert(err.message);
      })
  }

  useEffect(() => {
    listItem()
  }, [])


  return (

    <Router> 
    <section className='app'>
      <AuthProvider>
        <Switch> 
          <>
          <Route path="/admin" component={AdminHome} />
          <Route exact path="/add_products" component={AddProducts} />

          <section className="main_page_section">
          <Header/>
          <Route exact path="/" component={HomePage} />     
          <Route exact path="/breads">
            <Breads breads={breads}/>
          </Route>
          <Route exact path="/pastries" component={Pastries} />
          <Route exact path="/profile" component={Profile} />  
          <Route exact path="/login" component={Login} />        
          <Route exact path="/register" component={Register} /> 
        </section>
          </>

        </Switch>
        </AuthProvider>
    </section>
    </Router>

  )
}
