import { db } from './firebase'

export const initialState = {
    users: [],
    products: []

};


export const reducer = (prevState = initialState, action) => {
    const customersRef = db.collection('user')
    const productsRef = db.collection('product')


    switch(action.type) {

        case "fetch_products":
            let copyProducts = [...prevState.products]
            console.log(action)
            productsRef 
            .onSnapshot((snapshot) => {
                snapshot.docs.forEach( item => {
                    try{
                        if(copyProducts.length !== 0){
                            let itemExists = copyProducts.find( product => product.product_id === action.item.product_id);

                            if(!itemExists) {
                                copyProducts.push(item.data())
                            }
                        }else {
                            copyProducts.push(item.data())
                        }
                    }catch(error){
                        console.log(error)
                    }


                })
            })
            return{
                ...prevState, 
                products: copyProducts,
            }

        case "fetch_user_details":
            let copyUsers = [...prevState.users]

            let selectedUser;
            customersRef
                .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            selectedUser = user.data()
                            return selectedUser
                        })
                    })
                    .catch( (error) => console.log("Update Error: ", error))
            console.log(action, selectedUser)
            return{
                ...prevState,
                users: copyUsers,
            }


        default: 
            return prevState;
    }
            // return prevState;

}
