import React, { useEffect, useState } from 'react'
import './ViewCustomers.css'
import { db } from '../firebase'

import {Delete } from '@material-ui/icons';

export default function ViewCustomers({ customers }) {

    const [currentCustomers, setCurrentCustomers] = useState();
    const [isLoading, setIsLoading] = useState(false);

    const customersRef = db.collection('user')

    useEffect(() => {
        setCurrentCustomers(customers)
    }, [])


    // customersRef.onSnapshot( snapshot => {
    //     snapshot.docChanges().forEach( (change) => {
    //         if(change.type === "added"){
    //             console.log(change.doc.data())
    //         }else if(change.type === "removed"){
    //             console.log(change)
    //         }else if(change.type === "modified"){

    //         }

    //     })
    // })

    const handleDelete = async (e) => {
        e.preventDefault();
        let delUserID = e.target.parentNode.parentNode.parentNode.id;
        // if(window.confirm("Are You Sure you want to delete this record")){
            try{ 
                customersRef
                    .where("userID", "==", `${delUserID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            customersRef.doc(user.id).delete() 
                        })
                    })
                    .then( () => {
                        const filtered = customers.filter( (user) => user.userID !== delUserID );
                        // const userExists = users.find( person => person.userID === user.data().userID)  
                        setCurrentCustomers(filtered)
                    })
                    .catch(error => console.log("Deletion Error: ", error))         
            }catch(error){
                console.log("Deletion Error: ", error)
            }
        // }
    }


    return (
        <>
            <div className="welcome_bar">
            <h4 className="welcome_text">
                Cafe Kora Customers 
            </h4>
            <p className="welcome_ptag">Total Customers: {currentCustomers?.length}</p>

        </div>
        {

            currentCustomers?.length ?
        <>

        <div className="operations_section">

        </div>
        <div className="display_area">
            <table className="customers_table">
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email Address</th>
                        <th>Phone Number</th>
                        <th>Date Joined</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                {
                    currentCustomers.map( customer => 
                        <tr key={customer.userID} id={customer.userID}>
                            <td>{customer.fname}</td>
                            <td>{customer.lname}</td>
                            <td>{customer.email}</td>
                            <td>{customer.phone}</td>
                            <td>{customer.datejoined.toDate().toDateString()}</td>
                            <td>
                            <Delete onClick={handleDelete}/>
                            </td>
                        </tr>
                    )
                }
                </tbody>    

            </table>
        </div> 
        </>
            :
            <div className="welcome_bar">
                <p className="welcome_ptag">Loading Customers...</p>            
            </div>            
        }


        </>
    )
}
