import { Create, Delete, Home } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import { Link, useHistory } from 'react-router-dom';
import { useStateValue } from '../StateProvider'
import './Update.css'

export default function Update({match}) {

    const [ { products }] = useStateValue();
    const [currentP,setCurrentP ] = useState();
    const [isUpdate, setIsUpdate] = useState(false);
    const [uploadImage, setUploadImage] = useState();

    // ERROR HANDLING
    const [error, setError] = useState();
    const [isError, setIsError] = useState(false);

    useEffect(() => {
        // CHECK WHETHER UPDATE OR NEW PRODUCT
        // if(match.params.id){
            // setIsUpdate(true)
            products.map( prod => {
                if(prod.product_id === match.params.id){
                    console.log(prod)
                    return prod
                }
                setCurrentP(prod)   
            })
            console.log(currentP)
            // serveProduct()
        // }else {
        //     setIsUpdate(false)
        // }
    }, [])

    let name = document.getElementById("product_name");
    let descr = document.getElementById("product_descr");
    // let cat = document.getElementById("product_cat");
    let price = document.getElementById("product_price")
    let image = document.getElementById("product_image")

    const serveProduct = () => {
        if(currentP){
        name.placeholder = currentP.product_name
        descr.placeholder = currentP.product_descr
        // cat.placeholder = currentP.product_cat
        price.placeholder= currentP.product_price
        image.src= currentP.product_image
        }

    }

    const handleUpdate = () => {

    }

    const loadImage = (event) => {
        event.preventDefault();
        const image = document.querySelector("#display_userImage");
        if(event.target.files[0] !== undefined){
            image.src = URL.createObjectURL(event.target.files[0]);
            image.style.display = "block"
            setUploadImage(event.target.files[0])
        }else {
            image.src = "/images/avatar_placeholder.png";
        }
    }

    const handleChange = input => async (e) => {
        (input === "product_image") && loadImage(e)
    } 

    return (
        // <>
        // { 
        //     isUpdate ?
        //     <section className="update_section up_prod"> 

        //         <h2 className="add_prod_title">Cafe Kora Update Products</h2>

        //         <form onSubmit={handleUpdate} className="update_column">

        //             <div className="update_left registration_form">
        //                 <label className="form_labels">Upload Profile Picture:</label>
        //                 <img id="display_userImage" src="/images/avatar_placeholder.png" alt="Upload" />	

        //                 <input onChange={handleChange("product_image")} type="file" className="form_inputs" name="product_image" accept="image/*"/> 
        //             </div>

        //             <div className="update_right registration_form">
        //                 {
        //                 isError &&
