        //                 <label className="form_labels">Product Name:</label>
        //                 <input onChange={handleChange("product_name")} type="text" className="form_inputs" name="product_name" placeholder="{{}}"/>
        //                 <label className="form_labels">Product Description:</label>
        //                 <input onChange={handleChange("product_descr")} type="text" className="form_inputs" name="product_descr" />
        //                 <label className="form_labels">Product Category:</label>
        //                 <select onChange={handleChange("product_cat")} className="form_inputs" id="product_cat">
        //                     <option value="">Select Category:</option>
        //                     <option value="breads">Bread</option>
        //                     <option value="pastries">Pastry</option>
        //                 </select>
        //                 <label className="form_labels">Product Price:</label>
        //                 <input onChange={handleChange("product_price")} type="text" className="form_inputs" name="product_price" />

        //                 <button type="submit" className="form_btn ">Add Product</button>

        //             </div>


        //         </form>
        //             <div className="login_footer back_home_div add_footer">
        //                 <Link to="/"><Home />Back Home</Link>
        //             </div>
        //     </section>        

        //     :
            <section className="update_section up_prod"> 

            <h2 className="add_prod_title">Cafe Kora Update Products</h2>

            <form onSubmit={handleUpdate} className="update_column">

                <div className="update_left registration_form">
                    <label className="form_labels">Upload Product Photo:</label>
                    <img id="display_userImage" src="{{currentP.product_image}}" alt="Upload" />	

                    <input onChange={handleChange("product_image")} type="file" className="form_inputs" name="product_image" id="product_image" accept="image/*"/> 
                </div>

                <div className="update_right registration_form">
                    {
                    isError &&
                    <div className="form_error form_inputs">
                        <p>{error}</p>
                    </div> 

                    }
                    <label className="form_labels">Product Name:</label>
                    <input onChange={handleChange("product_name")} type="text" className="form_inputs" id="product_name" name="product_name" />
                    <label className="form_labels">Product Description:</label>
                    <input onChange={handleChange("product_descr")} type="text" className="form_inputs" name="product_descr" />
                    <label className="form_labels">Product Category:</label>
                    <select onChange={handleChange("product_cat")} className="form_inputs" id="product_cat">
                        <option value="">Select Category:</option>
                        <option value="breads">Bread</option>
                        <option value="pastries">Pastry</option>
                    </select>
                    <label className="form_labels">Product Price:</label>
                    <input onChange={handleChange("product_price")} type="text" className="form_inputs" name="product_price" />

                    <button type="submit" className="form_btn ">Add Product</button>

                </div>


                </form>
                <div className="login_footer back_home_div add_footer">
                    <Link to="/"><Home />Back Home</Link>
                </div>
            </section>        

        // }
        // </>
    )
}
