import { db } from './firebase'

export const initialState = {
    users: [],
    products: []

};


export const reducer = (prevState = initialState, action) => {
    const customersRef = db.collection('user')
    const productsRef = db.collection('product')

    switch(action.type) {

        case "delete_product" :
            let copyProducts =  [...prevState.products];
            console.log(action)
            try{ 
                productsRef
                    .where("product_id", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( item => {
                            productsRef.doc(item.id).delete() 
                        })
                    })
                    .then( () => {
                        let prodIndex = copyProducts.findIndex( product => product.product_id === action.targetID)                     
                        if (prodIndex >= 0) {
                            return copyProducts.splice(prodIndex, 1);
                        } 
                    })
                    .catch(error => console.log("Deletion Error: ", error))   


            }catch(error){
                console.log("Deletion Error: ", error)
            }            
            return {
                ...prevState, 
                products: [...copyProducts]                
            }
        case "fetch_products":
            let fetchedProducts = [...prevState.products];
            try{
                productsRef 
                .onSnapshot((snapshot) => {
                    snapshot.docChanges().forEach( item => {
                        // console.log(item.type)
                        // if(item.type === "added"){
                            if(fetchedProducts?.length === 0){
                                return fetchedProducts.push(item.doc.data())
                            }else{
                                let itemExists = fetchedProducts.findIndex( product => product.product_id === item.doc.data().product_id)

                                if(itemExists < 0){
                                    return fetchedProducts.push(item.doc.data())
                                }
                            }
                        // }else 
                        if(item.type === "removed"){
                            if(item.oldIndex >= 0){
                                return fetchedProducts.splice(item.oldIndex, 1)
                            }
                        }

                    })
                })
            }catch(error){
                console.log(error)
            }
            return{
                ...prevState, 
                products: [...fetchedProducts],
                // products: [...fetchedProducts],
            }

        case "fetch_user_details":
            let copyUsers = [...prevState.users]

            let selectedUser;
            customersRef
                .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            selectedUser = user.data()
                            return selectedUser
                        })
                    })
                    .catch( (error) => console.log("Update Error: ", error))
            console.log(action, selectedUser)
            return{
                ...prevState,
                users: copyUsers,
            }


        default: 
            return prevState;
    }
            // return prevState;

}
