import { db } from './firebase'

export const initialState = {
    users: [],
    products: []

};


export const reducer = (prevState = initialState, action) => {
    const customersRef = db.collection('user')

    switch(action.type) {

        case "update_user":
            let copyUsers = [...prevState.users]

            console.log(action, copyUsers)
            return{
                ...prevState,
                users: copyUsers,
            }


        default: 
            return prevState;
    }
