import { db } from './firebase'

export const initialState = {
    users: [],
    products: []

};


export const reducer = (prevState = initialState, action) => {
    const customersRef = db.collection('user')
    const productsRef = db.collection('product')


    switch(action.type) {

        case "delete_product" :
            let copyProducts =  [...prevState.products];
            console.log(action)
            try{ 
                customersRef
                    .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        console.log(snapshot)
                        snapshot.forEach( item => {
                            console.log(item)
                            // productsRef.doc(item.id).delete() 
                        })
                    })
                    .then( () => {
                        let filtered = copyProducts.filter( product => product.product_id !== action.targetID);

                        copyProducts = filtered
                        return filtered;
                    })
                    .catch(error => console.log("Deletion Error: ", error))   


            }catch(error){
                console.log("Deletion Error: ", error)
            }            
            return {
                ...prevState, 
                products: copyProducts,                
            }
        case "fetch_products":
            let fetchedProducts = [...prevState.products];
            try{
                productsRef 
                .onSnapshot((snapshot) => {
                    snapshot.docs.forEach( item => {
                        // fetchedProducts = [...prevState.products]
                        if(fetchedProducts?.length === 0){
                            return fetchedProducts.push(item.data())
                        }else{
                            let itemExists = fetchedProducts.findIndex( product => product.product_id === item.data().product_id)

                            if(itemExists < 0){
                                return fetchedProducts.push(item.data())
                            }
                        }
                    })
                })
            }catch(error){
                console.log(error)
            }
            return{
                ...prevState, 
                products: fetchedProducts,
                // products: [...fetchedProducts],
            }

        case "fetch_user_details":
            let copyUsers = [...prevState.users]

            let selectedUser;
            customersRef
                .where("userID", "==", `${action.targetID}`)
                    .get()
                    .then( snapshot => {
                        snapshot.forEach( user => {
                            selectedUser = user.data()
                            return selectedUser
                        })
                    })
                    .catch( (error) => console.log("Update Error: ", error))
            console.log(action, selectedUser)
            return{
                ...prevState,
                users: copyUsers,
            }


        default: 
            return prevState;
    }
            // return prevState;

}
