import React, { useEffect, useState } from 'react'
import { Link, Route } from 'react-router-dom';

import './AdminHome.css'

import { db } from '../firebase'
import { displayUser } from '../../actions/entry';
import {BusinessCenter, Collections, GroupWork, Home, Inbox, Language, People, Person, Queue, ShowChart } from '@material-ui/icons';
import MainDash from './MainDash';
import ViewCustomers from './ViewCustomers';
import ViewEmployees from './ViewEmployees';
import ViewProducts from './ViewProducts';
import AddProducts from './AddProducts';

/*
    1. DELETE USERS
    2. UPDATE USER INFO
    3. SORT USERS
    4. SEARCH USERS
*/
export default function AdminHome() {

    const [users, setUsers] = useState([]);
    const [team, setTeam] = useState([]);

    let customers = [];
    let admin = [];

    const getUsers = async () => {
        const customersRef = db.collection('user')
        const snapshot = 
        await customersRef
            .onSnapshot((snapshot) => {
                snapshot.docs.forEach( user => {

                    // customers.filter( cus => {

                    // })
                        user.data().type === 'customer' ?
                            // setUsers(prevState => [...prevState, user.data()])
                                customers.push(user.data())
                            : user.data().type === 'admin' &&
                                admin.push(user.data())                           
                            // setTeam(prevState => [...prevState, user.data()]) 

                        })
            }, error =>{
                console.log(error)
            })

        let uniqueCus = Array.from(new Set(customers.map( obj => JSON.stringify(obj)).map(str => JSON.parse(str))))
        let uniqueTeam = Array.from(new Set(admin.map( obj => JSON.stringify(obj)).map(str => JSON.parse(str))))
        setUsers(customers)         
        setTeam(uniqueTeam)
    }

    console.log(

    )
    useEffect(() => {
        // let isSubscribed = true
        getUsers()
        //     .then( data => {
        //     if (isSubscribed) {
        //         console.log(data)
        //     } 
        // }).catch(error => console.log(error))

        // return () => isSubscribed = false
    }, [])
    return (
        <section className="admin_main_body">
            <section className="admin_sidebar">
            <div className="sidebar_top">
                <img src="images/cafe_kora.png" alt="Logo" />
            </div> 

            <div className="sidebar_bottom">
                <p className="sidebar_parts">
                    <Home className="sidebar_icons"/>
                    <Link to="/admin">Home</Link>
                </p>

                <p className="sidebar_parts">
                    <People className="sidebar_icons"/>
                    <Link to="/admin/customers">Customers</Link>
                </p>

                <p className="sidebar_parts">
                    <GroupWork className="sidebar_icons"/>
                    <Link to="/admin/team">Cafe Kora Team</Link>
                </p>
                <p className="sidebar_parts">
                    <Collections className="sidebar_icons"/>
                    <Link to="/admin/products">Products</Link>
                </p>
                <p className="sidebar_parts">
                    <Queue className="sidebar_icons"/>
                    <Link to="/admin/add_products">Add Products</Link>
                </p>
                <p className="sidebar_parts">
                    <ShowChart className="sidebar_icons"/>Analytics
                </p>

                <p className="sidebar_parts">
                    <Inbox className="sidebar_icons"/>Categories
                </p>

                <p className="sidebar_parts">
                    <BusinessCenter className="sidebar_icons"/>Employees
                </p>

                <p className="sidebar_parts">
                    <Person className="sidebar_icons"/>Profile
                </p>

                <p className="sidebar_parts">
                    <Language className="sidebar_icons"/>
                    <Link to="/">Website Home</Link>
                </p>          
            </div>

            </section>

            <div className="dashboard_options_cont">
                {/* PUSH DATA ACCORDING TO THE USER CLICKS */}
                <Route exact path="/admin">
                    <MainDash customers={users} team={team}/>
                </Route>
                <Route exact path="/admin/customers">
                    <ViewCustomers customers={users}/>
                </Route> 
                <Route exact path="/admin/team">                            
                    <ViewEmployees team={team}/>
                </Route>  
                <Route exact path="/admin/add_products">
                    <AddProducts />
                </Route>      
                <Route exact path="/admin/products">
                    <ViewProducts />
                </Route>            
            </div>
        </section>
    )
}
